    display: 'flex',
    flexDirection: 'row',
    border: '1 solid black',
  },
  lang: {
    fontSize: 8,
    fontWeight: 700,
  },
  vote_average: {
    fontSize: 8,
    marginLeft: 10,
    marginRight: 10,
    fontWeight: 'bold',
