    textAlign: 'center',
    color: 'grey',
  },
  pdfHeadBlk: {
    marginBottom: 10,
    display: 'block',
  },
  pdfHeadTtl: {
    color: '#0ab7fd',
    fontSize: 16,
    marginBottom: 10,
    display: 'block',
  },
  pdfSubTtl: {
    color: '#39566b',
    fontSize: 13,
    marginBottom: 20,
    display: 'block',
  },
  meetingDate: {
    marginBottom: 10,
  },
  pdfMainWrap: {
    marginBottom: 20,
  },
  tblContainer: {
    // borderBottomWidth: 1,
    // borderBottomStyle: 'solid',
    // borderColor: '#dee2e6',
    display: 'block',
  },
  tblRow: {
    display: 'flex',
    flexDirection: 'row',
  },
  tblColOne: {
    padding: 8,
    verticalAlign: 'middle',
    fontSize: 11,
    color: '#40576a',
    fontWeight: 'normal',
    borderBottomWidth: 1,
    borderBottomStyle: 'solid',
    borderColor: '#dee2e6',
    width: '5' + '%',
