import { makeStyles, styled, withStyles } from '@material-ui/core/styles'
import { Link, Route, Switch, useHistory } from 'react-router-dom'

import { Box, Button, Divider, Paper, Grid, List, ListItem, ListItemText, ListItemAvatar, Avatar, Typography, InputAdornment, TextField, IconButton, Hidden, MenuItem, Badge, Menu, Dialog, DialogContent, DialogTitle, AppBar, Tabs, Tab, CardMedia } from '@material-ui/core'

import { Comment, Delete, Edit, Search, ArrowBack, Chat, Send, Print, Drafts, MoreVert, ThumbUp, Message, Close, FiberManualRecord, Check, Update } from '@material-ui/icons'
import FileCopyIcon from '@material-ui/icons/FileCopy';
import React, { useEffect, useMemo, useState } from 'react'
import { connect, useDispatch } from 'react-redux'
import {
  SuccessEvent
} from '../../store/actions/bookevent'

import {
  getUpcommingBookList, delete_booklist
} from '../../store/actions/booklist'
import moment from 'moment'
import { withSnackbar } from 'notistack'
import PropTypes from 'prop-types'
import FullscreenSpinner from '../../component/FullscreenSpinner'
import ResponsiveDialog from './Modal/RymindrModalDelete'
import Deleteevent from './Modal/BookingModelDelete'
import Comments from '../../container/Comments/Comments'
import RymindrOriginal from '../../assets/images/rymindr_original.png';

import axios from 'axios'
import clsx from 'clsx'
import Axios from '../../helper/axios'
import ReactDOM from 'react-dom'
import Pdf from 'react-to-pdf'
import parse from 'html-react-parser';
import io from 'socket.io-client';
function TabPanel (props) {
  const { children, value, index, ...other } = props

  return (
    <div
      role='tabpanel'
      hidden={value !== index}
      id={'simple-tabpanel-${index}'}
      aria-labelledby={'simple-tab-${index}'}
      {...other}
    >
      {value === index && (
        <Box>
          {children}
        </Box>
      )}
    </div>
  )
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired
}

function a11yProps (index) {
  return {
    id: `simple-tab-${index}`,
    'aria-controls': `simple-tabpanel-${index}`
  }
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    position: 'relative',
    height: 100 + '%'
  },
  rightPanel: {
    [theme.breakpoints.up('md')]: {
      display: 'block !important'
    }
  },
  icon: {
    width: 150,
    marginBottom: 40
  },
  content: {
    textAlign: "center",
    textAlign: "-webkit-center",
    paddingTop: "15%",
    height: "80vh",
  },

}))

const AvatarStyled = styled(Avatar)({
  height: 25,
  width: 25,
  '& img': {
    height: 'auto'
  }
})

const TypoTitleStyled = styled(Typography)(({ theme }) => ({
  fontSize: 14,
  color: theme.palette.secondary.contrastText,
  marginBottom: 10
}))

const TypoContentStyled = styled(Typography)(({ theme }) => ({
  fontSize: 16,
  color: theme.palette.text.primary
}))

const AvatarShareStyled = styled(Avatar)({
  height: 50,
  width: 50
})

const BoxStyled = styled(Box)({
  display: 'flex',
  '&>span': { marginRight: 10 }
})

const TypoHeadStyled = styled(Typography)(({ theme }) => ({
  fontSize: 24,
  color: theme.palette.primary.main,
  textTransform: 'uppercase',
  fontWeight: 'bold'
}))

const TypoListSubtext = styled(Typography)(({ theme }) => ({
  fontSize: 14,
  color: theme.palette.secondary.contrastText
}))

const ListStyled = styled(List)(({ theme }) => ({
  paddingLeft: 20
}))

const GridStyled = styled(Grid)({
  padding: '15px 15px 15px 30px'
})

const TextFieldStyled = styled(TextField)(({ theme }) => ({
  paddingBottom: 10, textAlign: 'left'
}))

const DialogTitleStyled = styled(DialogTitle)({
  borderBottom: '1px solid #e0e0e0',
  padding: '10px 24px',
  marginBottom: 20
})

const TypoPopHeadStyled = styled(Typography)(({ theme }) => ({
  color: theme.palette.secondary.contrastText,
  fontSize: 18,
  fontWeight: '600'
}))

const StyledBadge = withStyles((theme) => ({
  badge: {
    backgroundColor: '#44b700',
    color: '#44b700',
    boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
    '&::after': {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      borderRadius: '50%',
      animation: '$ripple 1.2s infinite ease-in-out',
      border: '1px solid currentColor',
      height: 20,
      width: 20,
      content: '""'
    }
  },
  '@keyframes ripple': {
    '0%': {
      transform: 'scale(.8)',
      opacity: 1
    },
    '100%': {
      transform: 'scale(2.4)',
      opacity: 0
    }
  }
}))(Badge)

const StyledBadgeReject = withStyles((theme) => ({
  badge: {
    backgroundColor: '#e50214',
    color: '#44b700',
    boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
    '&::after': {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      borderRadius: '50%',
      animation: '$ripple 1.2s infinite ease-in-out',
      border: '1px solid currentColor',
      content: '""'
    }
  },
  '@keyframes ripple': {
    '0%': {
      transform: 'scale(.8)',
      opacity: 1
    },
    '100%': {
      transform: 'scale(2.4)',
      opacity: 0
    }
  }
}))(Badge)

const StyledBadgePending = withStyles((theme) => ({
  badge: {
    backgroundColor: '#1773bf',
    color: '#1773bf',
    boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
    padding: 0,
    '&::after': {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      borderRadius: '50%',
      animation: '$ripple 1.2s infinite ease-in-out',
      border: '1px solid currentColor',
      content: '""'
    }
  },
  '@keyframes ripple': {
    '0%': {
      transform: 'scale(.8)',
      opacity: 1
    },
    '100%': {
      transform: 'scale(2.4)',
      opacity: 0
    }
  }
}))(Badge)



const AvatarComntStyled = styled(Avatar)({
  height: 42,
  width: 42,
  marginRight: 20
})

const TextComntFieldStyled = styled(TextField)({
  width: 100 + '%', height: 100
})

const TypoCmntName = styled(Typography)({
  color: '#757575',
  fontWeight: 'bold',
  fontSize: 12,
  '& span': {
    fontWeight: 'normal', marginLeft: 20
  }
})
const TypoCmntTxt = styled(Typography)({
  color: '#3d3d3d',
  fontSize: 14
})

const ButtonStyled = styled(Button)({
  color: '#757575',
  fontSize: 12
})

const ButtonPlain = styled(Button)(({ theme }) => ({
  color: '#98a5af',
  fontSize: 12,
  textTransform: 'capitalize',
  background: 'none',
  boxShadow: 'none'
}))

const TypoTimeStyled = styled(Typography)(({ theme }) => ({
  fontSize: 14,
  color: theme.palette.primary.main,
  fontWeight: 'bold'
}))

const TypoStatusStyled = styled(Typography)(({ theme }) => ({
  fontSize: 14,
  // color: theme.palette.secondary.contrastText,
  color: '#1773bf',
  fontWeight: '600'
}))

const TypoStatusStyledAccept = styled(Typography)(({ theme }) => ({
  fontSize: 14,
  color: '#44b700',
  fontWeight: '600'
}))

const TabsStyles = styled(Tabs)(({ theme }) => ({
  borderBottom: '1px solid #ccc',
  '& span': {
    justifyContent: 'center'
  }
}))

const Bookings = (props) => {
  const { user_id, business_code, first_name, last_name } = JSON.parse(localStorage.getItem('userData'))
  const {
    enqueueSnackbar,
    error,
    success_message,
    sucessEvent,
    SuccessEvent,
    booklistdata1,
    getUpcommingBookList,
    delete_booklist,
    loading
  } = props

  const [userStatus, setUserStatus] = useState({})
  const [booklistdata, setBooklistdata] = useState([])
  const [allBooklistdata, setAllBooklistdata] = useState([])
  const [defaultSelected, setDefaultSelected] = useState('')
  console.log('booklistdata1--',booklistdata1)
  console.log('SuccessEvent--',SuccessEvent)


  useEffect(() => {
    setBooklistdata(booklistdata1);
    setAllBooklistdata(booklistdata1);
  }, [booklistdata1])

  useEffect(() => { 
    getUpcommingBookList({ user_id })
    if (!loading) {
      SuccessEvent(false)
    }
  }, [getUpcommingBookList])

  useEffect(() => {
    async function fetchUserStatus (ID, meeting_id) {
      const result = await Axios().post('bookevent/eventuserstatus', { event_id: ID, meeting_id: meeting_id })
      setUserStatus(result.data.data)
    }

    console.log('Data----',props.location.state)
    if (booklistdata != null) {
      if (booklistdata[0] == undefined) {

      } else if(props.location && props.location.state && props.location.state.post_id && props.location.state.post_id != null) {
        if(props.location.state.recr_post_id != 0) {
          setDefaultSelected(props.location.state.recr_post_id)
        }
        else {
          setDefaultSelected(props.location.state.post_id)
        }
        } else {
        setSelectedIndex(booklistdata[0] && booklistdata[0].meeting_data && booklistdata[0].meeting_data[0].meet_id)
        fetchUserStatus(booklistdata[0] && booklistdata[0].meeting_data && booklistdata[0].id, booklistdata[0].meeting_data[0].meet_id)
      }
    }
  }, [booklistdata])

  // useEffect(() => {
  //   if(props.location.state.post_id != null) {
  //     setSelectedIndex(props.location.state.post_id)
  //   }
  // }, []);

  const history = useHistory()

  console.log('history====',history);
  const [value, setValue] = React.useState(0)

  const handleTabChange = (event, newValue) => {
    setValue(newValue)
  }

  const [showPanel, setShowPanel] = useState(true)

  const [selectedIndex, setSelectedIndex] = React.useState(1)

  const [openRymindr, setOpenRymindr] = useState(false)
  const handleCloseRymindr = (newValue) => {
    // alert('main'+newValue);
    setOpenRymindr(newValue)
  }

  const handleToopenRymindr = () => {
    setOpenRymindr(true)
  }

  const handleDelete = (eventId, user_id) => {
  // alert(user_id);
    // setOpenDelete(true);
    setOpenRymindr(true)
    const setdata = {
      eventId,
      user_id
    }
    setDeletebooklist(setdata)
  }

  const handleShowDetail = () => {
    // alert('ababa');
    setShowPanel(false)
  }

  const handleHideDetail = () => {
    setShowPanel(true)
  }

  const classes = useStyles()

  const currencies = [
    {
      value: 'Parents Evening',
      label: 'Parents Evening',
      category_id: 159,
      img: require('../../assets/images/parent.png')
    },
    {
      value: 'Event',
      label: 'Event',
      category_id: 158,
      img: require('../../assets/images/event.png')
    },
    {
      value: 'Appointment',
      label: 'Appointment',
      category_id: 157,
      img: require('../../assets/images/event_blue.png')
    },
    {
      value: 'all',
      label: 'All'
    }
  ]

  const [currency, setCurrency] = React.useState('all')
  const [currencyValue, setCurrencyValue] = React.useState('all')

  const handleChange = (event) => {
    let selCategory = currencies.find(o => o.value === event.target.value);
    setCurrency(selCategory.category_id)
    setCurrencyValue(selCategory.value)

    let temp = [];

    if(selCategory.value != 'all') {
      allBooklistdata.map((item, index) => {

        if(selCategory.category_id == item.category) {
          temp.push(item);
        }
      })

    } else {
      allBooklistdata.map((item, index) => {
          temp.push(item);
      })

    }
    setBooklistdata(temp)

  }

  const [anchorEl, setAnchorEl] = React.useState(null)

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget)
  }

  const handleClose = () => {
    setAnchorEl(null)
  }

  const handleClose1 = () => {
    setOpenDelete(false)
  }

  const [open, setOpen] = React.useState(false)

  const handleClickOpen = () => {
    setOpen(true)
  }

  const handleCloseModal = () => {
    setOpen(false)
  }
  const [eventId, setEventId] = React.useState(null)
  const [bookid, setBookid] = React.useState(0)



  useEffect(() => {
    let mounted = true
    var sock = io("https://app.rymindr.com:8081");
    sock.on('booking-status:App\\Events\\BookingStatus', function (data){
        if (mounted) {
            if(data.res.post_id==eventId && data.res.meeting_id==selectedIndex){
              async function fetchUserStatus (ID) {
                const result = await Axios().post('bookevent/eventuserstatus', { event_id: ID, meeting_id: selectedIndex })
                setUserStatus(result.data.data)
              }
              console.log('Changed=');
              fetchUserStatus(eventId)
            }
        }
    })
    return function cleanup() {
        mounted = false
    }
  }, [eventId,selectedIndex])


  const handleInputChange = (event, index, tabindex) => {
    setDefaultSelected('')
    setEventId(index)
    setSelectedIndex(tabindex)
    async function fetchUserStatus (ID) {
      const result = await Axios().post('bookevent/eventuserstatus', { event_id: ID, meeting_id: tabindex })
      setUserStatus(result.data.data)
    }
    fetchUserStatus(index)
  }

  const [deletebooklist, setDeletebooklist] = useState(null)
  const [openDelete, setOpenDelete] = useState(false)

  const deleteRymindr = (data) => {
    const newItem = Object.assign({}, deletebooklist, data)
    // setOpenDelete(false);
    delete_booklist(newItem)
  }

  const StatusHead = userStatus !== null && userStatus.length > 0 ? (userStatus.map((item, index) =>
    (
      <Tab label={item && item.length > 0 && item[0].meeting_date} {...a11yProps(index)} />

    ))) : (<Tab label='28 APR 2020 {value}' {...a11yProps(0)} />)

  const StatusTab = userStatus !== null && userStatus.length > 0 ? (userStatus.map((item, index) =>
